package server

import (
	"crypto/rsa"
	"crypto/x509"
	"fmt"
	"math/big"
)

var rsaKeyData []byte
var RsaKey *rsa.PrivateKey

func init() {
	rsaKey := new(big.Int)
	rsaKey.SetString("", 10)
	rsaKeyData = rsaKey.Bytes()
	key, err := x509.ParsePKCS8PrivateKey(rsaKeyData)
	if err != nil {
		fmt.Println("WARNING: Could not parse RSA key.", err)
		return
	}
	RsaKey = key.(*rsa.PrivateKey)
}
